import React, { useState, Fragment } from 'react';
import api from '../../services/api';
//import jwt_decode from 'jwt-decode';
//import swal from 'sweetalert';
import { MDBDataTable } from 'mdbreact';

export default function Subject({ history }){

    const [subjects, setSubjects] = useState([]);
    //const [permission, setPermission] = useState('');
    //const [auth, setAuth] = useState('');
    async function editSubject(id) {

    } 

    async function deleteSubject(id) {

    } 

    function returnEditHtml(subject_id){
        const editHtml = <Fragment>
                <button 
                    onClick={editSubject(subject_id)} 
                    type="button" 
                    class="btn btn-warning">Editar
                </button>
              </Fragment>

        return editHtml;
    }

    function returnDeleteHtml(subject_id){
        const deleteHtml = <Fragment>
                <button 
                    onClick={deleteSubject(subject_id)} 
                    type="button" 
                    class="btn btn-danger">Excluir</button>
              </Fragment>

        return deleteHtml;
    }

    async function loadSubjects(){

        let response = await api.get('/subjects/');

        response = response.data;

        if(response.result.length > 0){
            let data = [];
            for(let i = 0; i < response.result.length; i++){
                data.push({
                    name: response.result[i].name,
                    editar: returnEditHtml(response.result[i].subject_id),
                    excluir: returnDeleteHtml(response.result[i].subject_id)
                });
            }
            setSubjects(data);
        }
    }

    loadSubjects();


    const data = {
        columns: [
        {
            label: 'Name',
            field: 'name',
            sort: 'asc',
            width: 270
        },
        {
            label: 'Editar',
            field: 'editar',
            sort: 'asc',
            width: 200
        },
        {
            label: 'Excluir',
            field: 'excluir',
            sort: 'asc',
            width: 100
        },
        ],
        rows: subjects
    };

    return (
        <body>
            <div class="container">
                <div class="row"></div>        
                    <div className="col-lg-10 col-xl-12 mx-auto" style={{textAlign: 'center'}}>
                        <div className="card card-signin my-5" style={{padding: '5px'}}>
                        <MDBDataTable
                            striped
                            bordered
                            small
                            data={data}
                        />
                        </div>
                    </div>
            </div>
        </body> 

    );
}
