import React, { useEffect, useState } from 'react';
import api from '../../services/api';

export default function Mainmenu(){
    const [ permission, setPermission ] = useState('');

    useEffect(() =>{
        async function loadUser(){
            const user_id = localStorage.getItem('user_id');
            const authorization = localStorage.getItem('authorization');
            setPermission(1);
            if(!user_id && !authorization){
                const response = await api.get(`/users/${user_id}`, {
                    headers: {authorization},
                });

                setPermission(response.data.result.permission);
            }
        }

        loadUser();
    }, []);

    return (
        <>
            <div className="collapse navbar-collapse" id="navbarSupportedContent">
                <ul className="navbar-nav ml-auto mt-2 mt-lg-0">
                    <li className="nav-item active">
                        <a className="nav-link" href="/#">Home <span className="sr-only">(current)</span></a>
                    </li>
                    <li className="nav-item">
                        <a className="nav-link" href="/#">Cursos</a>
                    </li>
                    {(permission === 1) ?  
                    <li className="nav-item dropdown">
                        <a className="nav-link dropdown-toggle" href="/#" id="navbarDropdown" role="button" data-toggle="dropdown" aria-haspopup="true" aria-expanded="false">
                        Gerência
                        </a>
                        <div className="dropdown-menu dropdown-menu-right" aria-labelledby="navbarDropdown">
                            <a className="dropdown-item" href="/#">Dashboard</a>
                            <a className="dropdown-item" href="/#">Disciplinas</a>
                            <a className="dropdown-item" href="/#">Artigos</a>
                            <a className="dropdown-item" href="/#">Cursos</a>
                            <a className="dropdown-item" href="/#">Usuários</a>
                        </div>
                    </li> : ''
                    }
                    <li className="nav-item dropdown">
                        <a className="nav-link dropdown-toggle" href="/#" id="navbarDropdown" role="button" data-toggle="dropdown" aria-haspopup="true" aria-expanded="false">
                        Usuário
                        </a>
                        <div className="dropdown-menu dropdown-menu-right" aria-labelledby="navbarDropdown">
                            <a className="dropdown-item" href="/#">Login</a>
                            <a className="dropdown-item" href="/#">Cadastro</a>
                        </div>
                    </li>
                </ul>
            </div>
        </>
    );
}
