import React, { useState, Fragment } from 'react';
import api from '../../services/api';
import { MDBDataTable } from 'mdbreact';
import { FaPlus, FaPen, FaTrash } from "react-icons/fa";
import { Button, Modal } from 'react-bootstrap';
import swal from 'sweetalert';
import jwt_decode from 'jwt-decode';

export default function Subject({ history }){
    const [name, setName] = useState('');
    const [subjects, setSubjects] = useState([]);
    const [show, setShow] = useState(false);
    const submitButton = React.createRef(null);
    const handleClose = () => setShow(false);
    const handleShow = () => setShow(true);
    let auth = [];

    function initialize(){
        const authorization = localStorage.getItem('61757468');

        if(!authorization){
            history.push('./Login')
        }else{
            let auth_decode = jwt_decode(authorization);

            let obj = [{permission: auth_decode.permission,
                authorization
            }];

            auth = obj;
            if(auth[0].permission !== 1){
                history.push('./Login')
            }
        }
    }

    initialize();

    async function edit(event, id) {
        event.preventDefault();
        console.log(id)
    } 

    async function del(event, id) {
        event.preventDefault();
        const willDelete = await swal({
            title: "Você tem certeza que irá excluir?",
            icon: "warning",
            dangerMode: true,
        });
        if (willDelete) {
            let response = await api.delete(`/subjects/${id}`, 
            {headers: { authorization: auth[0].authorization }});

            response = response.data;

            if(response.result.length > 0){
                swal("Exclusão realizada com sucesso");
            }else{
                document.getElementById("message").innerHTML = "Não foi possível excluir!";
            } 
        }
    }

    function returnEditHtml(id){
        const editHtml = <Fragment>
                            <button 
                                onClick={(e) => {edit(e, id)}} 
                                type="button" 
                                className="btn btn-warning"><FaPen />
                            </button>
                        </Fragment>

        return editHtml;
    }

    function returnDeleteHtml(id){
        const deleteHtml =  <Fragment>
                                <button 
                                    onClick={(e) => {del(e, id)}}
                                    type="button" 
                                    className="btn btn-danger"><FaTrash />
                                </button>
                            </Fragment>

        return deleteHtml;
    }

    async function loadSubjects(){

        let response = await api.get('/subjects/');

        response = response.data;

        if(response.result.length > 0){
            let data = [];
            for(let i = 0; i < response.result.length; i++){
                data.push({
                    name: response.result[i].name,
                    editar: returnEditHtml(response.result[i].subject_id),
                    excluir: returnDeleteHtml(response.result[i].subject_id)
                });
            }
            setSubjects(data);
        }
    }

    loadSubjects();


    const data = {
        columns: [
        {
            label: 'Nome',
            field: 'name',
            sort: 'asc',
            width: 270
        },
        {
            label: 'Editar',
            field: 'editar',
            sort: 'asc',
            width: 200
        },
        {
            label: 'Excluir',
            field: 'excluir',
            sort: 'asc',
            width: 100
        },
        ],
        rows: subjects
    };

    async function handleSubmit(event) {
        event.preventDefault();

        let response = await api.post('/subjects', { name }, 
        {headers: { authorization: auth[0].authorization }});

        response = response.data;

        if(response.result.length > 0){
            loadSubjects();
            handleClose();
            swal("Cadastro realizado com sucesso");
        }else{
            document.getElementById("message").innerHTML = "Não foi possível cadastrar!";
        }          
    }

    return (
        <>
            <div className="container">
                <div className="row"></div>        
                    <div className="col-lg-10 col-xl-12 mx-auto" style={{textAlign: 'center'}}>
                        <div className="card card-signin my-5" style={{padding: '5px'}}>
                        <button onClick={handleShow}
                            className="btn btn-success col-lg-2" 
                            style={{marginLeft:'85%',width:'48px',height:'48px',borderRadius:'100%'}}>
                            <FaPlus />
                        </button>  
                        <MDBDataTable
                            striped
                            bordered
                            small
                            data={data}
                        />
                        </div>
                    </div>
            </div>

            <Modal show={show} onHide={handleClose} animation={false}>
                <Modal.Header closeButton>
                    <Modal.Title>Nova disciplina</Modal.Title>
                </Modal.Header>
                <Modal.Body>
                    <form className="form-signin" onSubmit={handleSubmit}>
                        <div className="form-label-group">
                            <input type="text" 
                            id="inputName" 
                            className="form-control" 
                            placeholder="Nome da disciplina" required autofocus
                            value={name}
                            onChange={event => setName(event.target.value)}/>
                            <label htmlFor="inputName"> Nome*</label>
                        </div>

                        <div id="message" align='center'></div>

                        <button ref={submitButton} type="submit" hidden></button>

                    </form>
                </Modal.Body>
                <Modal.Footer>
                    <Button variant="secondary" onClick={handleClose}>
                        Fechar
                    </Button>
                    <Button variant="primary" 
                        onClick={e => { submitButton.current.click(); }}>
                        Salvar
                    </Button>
                </Modal.Footer>
            </Modal>
        </> 

    );
}
