import React, { useState, Fragment, Modal, Button } from 'react';
import api from '../../services/api';
//import jwt_decode from 'jwt-decode';
//import swal from 'sweetalert';
import { MDBDataTable } from 'mdbreact';
import { FaPlus, FaPen, FaTrash } from "react-icons/fa";

export default function Subject({ history }){

    const [subjects, setSubjects] = useState([]);
    const [show, setShow] = useState(false);

    const handleClose = () => setShow(false);
    const handleShow = () => setShow(true);

    //const [permission, setPermission] = useState('');
    //const [auth, setAuth] = useState('');

    async function editSubject(e, id) {
        console.log(id)
    } 

    async function deleteSubject(e, id) {
        console.log(id)
    } 

    function returnEditHtml(subject_id){
        const editHtml = <Fragment>
                            <button 
                                onClick={(e) => {editSubject(e, subject_id)}} 
                                type="button" 
                                className="btn btn-warning"><FaPen />
                            </button>
                        </Fragment>

        return editHtml;
    }

    function returnDeleteHtml(subject_id){
        const deleteHtml =  <Fragment>
                                <button 
                                    onClick={(e) => {deleteSubject(e, subject_id)}}
                                    type="button" 
                                    className="btn btn-danger"><FaTrash />
                                </button>
                            </Fragment>

        return deleteHtml;
    }

    async function loadSubjects(){

        let response = await api.get('/subjects/');

        response = response.data;

        if(response.result.length > 0){
            let data = [];
            for(let i = 0; i < response.result.length; i++){
                data.push({
                    name: response.result[i].name,
                    editar: returnEditHtml(response.result[i].subject_id),
                    excluir: returnDeleteHtml(response.result[i].subject_id)
                });
            }
            setSubjects(data);
        }
    }

    loadSubjects();


    const data = {
        columns: [
        {
            label: 'Nome',
            field: 'name',
            sort: 'asc',
            width: 270
        },
        {
            label: 'Editar',
            field: 'editar',
            sort: 'asc',
            width: 200
        },
        {
            label: 'Excluir',
            field: 'excluir',
            sort: 'asc',
            width: 100
        },
        ],
        rows: subjects
    };

    return (
        <>
            <div className="container">
                <div className="row"></div>        
                    <div className="col-lg-10 col-xl-12 mx-auto" style={{textAlign: 'center'}}>
                        <div className="card card-signin my-5" style={{padding: '5px'}}>
                        <button onClick={handleShow}
                            className="btn btn-success col-lg-2" 
                            style={{marginLeft:'85%',width:'48px',borderRadius:'100%'}}>
                            <FaPlus />
                        </button>  
                        <MDBDataTable
                            striped
                            bordered
                            small
                            data={data}
                        />
                        </div>
                    </div>
            </div>

            <Modal show={show} onHide={handleClose} animation={false}>
                <Modal.Header closeButton>
                    <Modal.Title>Modal heading</Modal.Title>
                </Modal.Header>
                <Modal.Body>Woohoo, you're reading this text in a modal!</Modal.Body>
                <Modal.Footer>
                    <Button variant="secondary" onClick={handleClose}>
                        Close
                    </Button>
                    <Button variant="primary" onClick={handleClose}>
                        Save Changes
                    </Button>
                </Modal.Footer>
            </Modal>
        </> 

    );
}
