import React, { useState, useEffect } from 'react';
import api from '../../services/api';
import jwt_decode from 'jwt-decode';
import swal from 'sweetalert';
import { MDBDataTable } from 'mdbreact';
import ReactHtmlParser from 'react-html-parser'; 

export default function Subject({ history }){

    const [subjects, setSubjects] = useState([]);
    const [permission, setPermission] = useState('');
    const [auth, setAuth] = useState('');

    useEffect(() =>{
        async function loadSubjects(){

            let response = await api.get('/subjects/');

            response = response.data;

            if(response.result.length > 0){
                let data = [];
                for(let i = 0; i < response.result.length; i++){
                    data.push({
                        name: response.result[i].name,
                        editar: `<button 
                        onClick={editSubject(${response.result[i].subject_id})} 
                        type="button" 
                        class="btn btn-warning">Editar</button>`,
                        excluir: `<button 
                        onClick={deleteSubject(${response.result[i].subject_id})} 
                        type="button" 
                        class="btn btn-danger">Excluir</button>`
                    });
                }
                setSubjects(data);
            }
        }

        loadSubjects();
    }, []);

    const data = {
        columns: [
        {
            label: 'Name',
            field: 'name',
            sort: 'asc',
            width: 270
        },
        {
            label: 'Editar',
            field: 'editar',
            sort: 'asc',
            width: 200
        },
        {
            label: 'Excluir',
            field: 'excluir',
            sort: 'asc',
            width: 100
        },
        ],
        rows: subjects
    };

    return (
        <body>
            <div class="container">
                <div class="row"></div>        
                    <div className="col-lg-10 col-xl-12 mx-auto" style={{textAlign: 'center'}}>
                        <div className="card card-signin my-5" style={{padding: '5px'}}>
                        <MDBDataTable
                            striped
                            bordered
                            small
                            data={data}
                        />
                        </div>
                    </div>
            </div>
        </body> 

    );
}
