import jwt_decode from 'jwt-decode';
import api from './services/api';

export async function initialize({history}){
    const authorization = localStorage.getItem('61757468');

    if(!authorization){
        history.push('./Login')
    }else{
        let auth_decode = jwt_decode(authorization);

        let response = await api.get(`/users/${auth_decode.user_id}`, {
            headers: {authorization},
        });

        if(response.data.result.length > 0){
            let obj = [{permission: auth_decode.permission,
                authorization
            }];

            if(obj[0].permission !== 1){
                history.push('/')
            }

            return obj;
        }else{
            history.push('./Login')
        }
    }
}
