import React, { Fragment } from 'react';
import api from './services/api';
import { FaPen, FaTrash } from "react-icons/fa";
import swal from 'sweetalert';
import jwt_decode from 'jwt-decode';

export function initialize({history}){
    const authorization = localStorage.getItem('61757468');

    if(!authorization){
        history.push('./Login')
    }else{
        let auth_decode = jwt_decode(authorization);

        let obj = [{permission: auth_decode.permission,
            authorization
        }];

        if(obj[0].permission !== 1){
            history.push('./Login')
        }
        return obj;
    }
}

async function edit(event, id) {
    event.preventDefault();
    console.log(id)
} 

async function del(event, id, route, authorization) {
    event.preventDefault();
    const willDelete = await swal({
        title: "Você tem certeza que irá excluir?",
        icon: "warning",
        dangerMode: true,
    });
    if (willDelete) {
        let response = await api.delete(`/${route}/${id}`, 
        {headers: { authorization }});

        response = response.data;

        if(response.result.length > 0){
            swal("Exclusão realizada com sucesso");
        }else{
            document.getElementById("message").innerHTML = "Não foi possível excluir!";
        } 
    }
}

export function returnDeleteHtml(id, route, authorization){
    const deleteHtml =  <Fragment>
                            <button 
                                onClick={(e) => {del(e, id, route, authorization)}}
                                type="button" 
                                className="btn btn-danger"><FaTrash />
                            </button>
                        </Fragment>

    return deleteHtml;
}

export function returnEditHtml(id){
    const editHtml = <Fragment>
                        <button 
                            onClick={(e) => {edit(e, id)}} 
                            type="button" 
                            className="btn btn-warning"><FaPen />
                        </button>
                    </Fragment>

    return editHtml;
}
